mapper.select:
select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.query_param, m.visible, m.status, m.perms, m.is_frame, m.is_cache, m.menu_type, m.icon, m.order_num, m.create_time from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id left join sys_user_role sur on rm.role_id = sur.role_id left join sys_role ro on sur.role_id = ro.role_id
sur.user_id = #{userId}
AND m.menuName like concat('%',#{menu.menuName,jdbcType=VARCHAR},'%')
AND m.visible = #{menu.visible,jdbcType=VARCHAR}
AND m.status = #{menu.status,jdbcType=VARCHAR}
order by m.parent_id ,m.order_num asc

select m.menu_id from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id where rm.role_id = #{roleId}
and m.menu_id not in (select m.parent_id from sys_menu m inner join sys_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num
